  static Future<List<Question>> readFromFireStore() async {
    List<Question> questionList = List<Question>();
    await databaseReference
        .collection("free")
        .get()
        .then((QuerySnapshot snapshot) {
      snapshot.docs.forEach((document) {
        Map questionDoc = document.data();

        //1) create varibale type Question;
        int index = 0;
        if (questionDoc['ANSWERS'] == questionDoc['A'])
          index = 0;
        else if (questionDoc['ANSWERS'] == questionDoc['B'])
          index = 1;
        else if (questionDoc['ANSWERS'] == questionDoc['C'])
          index = 2;
        else if (questionDoc['ANSWERS'] == questionDoc['D']) index = 3;
        print("AKE: ${questionDoc['A']}");
        List<String> answers = [
          questionDoc['A'].toString(),
          questionDoc['B'].toString(),
          questionDoc['C'].toString(),
          questionDoc['D'].toString()
        ];
        Question questionObject = Question(
            answers: answers,
            correctAnswerIndex: index,
            question: questionDoc['QUESTION']);
        questionList.add(questionObject);
      });
    });
    print("List Lenght:  ${questionList.length}");
    return questionList;
  }

  static Future<int> getNumberOfQuestion() async {
    int numberOfQuestions = 0;
    await databaseReference
        .collection("constrains")
        .get()
        .then((QuerySnapshot snapshot) {
//      numberOfQuestions =int.parse(snapshot.docs[0].data()["number_of_questions_per_exam"]);
      numberOfQuestions =
          snapshot.docs[0].data()["number_of_questions_per_exam"];
    });
    return numberOfQuestions;
  }

  static Future<List<Category>> getCategories() async {
    int numberOfQuesitons = await getNumberOfQuestion();
    List<Question> questionList = await readFromFireStore();
    int numberOfTest = (questionList.length / numberOfQuesitons).truncate();
    List<Category> categories = List<Category>();
    for (int i = 1; i <= numberOfTest; i++) {
      categories.add(Category(name: "Test #$i", id: i));
    }
    return categories;
  }

